sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    //gs.log('In generate quote br');
    var gr = new GlideRecord("x_1386762_sales_cr_quote");
    gr.addEncodedQuery('u_oppname.numberSTARTSWITH' + current.number + '^u_status=draft');
    gr.query();

	gr.next();
gs.info(gr.sys_id);
	if(gr.getRowCount() == 1 ){

		var gr1 = new GlideRecord('x_1386762_sales_cr_quote');
		gr1.addEncodedQuery('sys_idSTARTSWITH'+gr.sys_id);
		gr1.query();

		if(gr1.next()){
			gr1.u_grandtotal = current.u_amount;
			gr1.update();
		}
	}
    else if (current.u_grandtotalamount == 0) {

        gr.initialize();
        gr.u_oppname = current.sys_id;
        gr.u_quotename = current.u_contactname + "-" + current.u_oppname;
        gr.u_grandtotal = current.u_amount;
        gr.u_reference_6 = current.u_contactname;
        gr.u_email = current.u_contactemail;
        gr.u_phone = current.u_contactphone;
        gr.insert();

    }

})(current, previous);




