sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

    if (current.operation() == 'insert') {
        var gr = new GlideRecord('x_1386762_sales_cr_forecast');
        gr.initialize();
        gr.u_oppname = current.sys_id;

        gr.u_opportuntiyname = current.u_oppname;
        gr.u_accname = current.u_accname;
        gr.u_amount = current.u_amount;
        gr.u_closedate = current.u_closedate;
        gr.u_stage = current.getValue('u_stage');
        gr.u_probability = "20%";
        gr.u_forecategory = "pipeline";
        gr.insert();

    } else if (current.operation() == 'update') {
        var rec = new GlideRecord('x_1386762_sales_cr_forecast');
        rec.addQuery('u_oppname', current.sys_id);
        rec.query();
        if (rec.next()) {
            rec.u_opportuntiyname = current.u_oppname;
            // rec.u_amount = current.u_grandtotalamount;
            rec.u_stage = current.getValue('u_stage');

            rec.u_accname = current.u_accname;
            rec.u_closedate = current.u_closedate;
            // rec.u_forecategory = 'closed';
            // rec.u_probability = '100%';
            if (current.u_stage == 'qualification') {
                rec.u_probability = '40%';
            } else if (current.u_stage == 'proposal/price quote') {
                rec.u_probability = '60%';
            } else if (current.u_stage == 'negotiation/review') {
                rec.u_probability = '80%';
            } else if (current.u_stage == 'closed won') {
                rec.u_forecategory = 'closed';
                rec.u_probability = '100%';
            } else if (current.u_stage == 'closed lost') {
                rec.u_forecategory = 'omitted';
                rec.u_probability = '0%';
            } else {
                rec.u_forecategory = 'pipeline';
            }
            //--------------------------------------------------------

            if (current.u_grandtotalamount == 0) {
                rec.u_amount = current.u_amount;

            } else {
                rec.u_amount = current.u_grandtotalamount;
            }

            rec.update();


        }

    }

})(current, previous);
