function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }


    var ga = new GlideAjax('Populate_Price_in_Order');
    ga.addParam('sysparm_name', 'populatePrice');
    ga.addParam('productItemSysId', g_form.getValue('u_productitem'));
    ga.getXML(information);

    function information(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        var result = JSON.parse(answer);

        var productPrice = g_form.setValue('u_productprice', result.productPrice);
        g_form.setValue('u_productdescription', result.productDesc);
        // if(g_form.getValue('u_quantity') != "")
        // {
        // 	g_form.setValue('u_grandtotal',(parseInt(g_form.getValue('u_quantity')) * parseInt(productPrice)));
        // }






    }









    //    alert(g_form.getValue('u_productitem'));

    //Type appropriate comment here, and begin script below

}
