sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below


    if (g_form.getValue('number') != "") {
        var ga = new GlideAjax('storegrandtotal');
        ga.addParam('sysparm_name', 'storegrandtotal');
        ga.addParam('sysparm_value', g_form.getValue('number'));
        ga.getXML(populateGroup);
    }



    function populateGroup(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        var arr = answer.split(",");
        g_form.setValue('u_amount', arr[0]);
        g_form.setValue('u_grandtotal', arr[1]);



    }
}
