sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    // Function to validate email format
    function isValidEmail(email) {
        // Regular expression for email validation
        var emailRegex = /[a-z][a-zA-Z0-9.-]{3,15}@[a-zA-Z0-9]{3,10}.(com|co.in)/;
        return emailRegex.test(email);
    }

    var fieldValue = newValue;
    if (fieldValue && !isValidEmail(fieldValue)) {
        // Display error message and revert field value
        g_form.setValue('u_email', oldValue);
        g_form.showErrorBox('u_email', 'Please enter a valid email address.');

    }
}
