sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var productItem = g_form.getValue('u_productitem');

    // Display the productItem value
    alert(productItem);

    // Check if the reference field is not empty
    if (productItem) {
        // Get the value of 'u_itemprice' from the referenced record
        var itemPrice = productItem.getValue('u_itemprice');

        // Check if the 'u_itemprice' field is not empty
        if (itemPrice !== '') {
            // Set the value of 'u_productprice' to the item price
            g_form.setValue('u_productprice', itemPrice);

            // Optionally, you can add an alert to indicate success
            alert("Product price set successfully.");
        } else {
            // Handle the case where 'u_itemprice' is empty
            // You may want to display a message or take appropriate action
            alert("Item price is empty.");
        }
    } else {
        // Handle the case where 'u_productitem' is empty
        // You may want to display a message or take appropriate action
        alert("Product item is empty.");
    }
}
