sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here

    var gr = new GlideRecord('x_1386762_sales_cr_leads');

    gr.addQuery('number', current.number);

    gr.query();
    while(gr.next()) {
        template.print('Lead Name : ' + gr.u_salutation+' ' + gr.u_firstname + ' ' + gr.u_lastname + "<br>"
		+'Lead Phone : ' + gr.u_phone + "<br>"
		+'Lead Email : ' + gr.u_email + "<br>"
		+'Lead Company : ' + gr.u_company + "<br>"
		+ 'Lead Owner : ' + gr.u_referenceleadowner.getDisplayValue() + "<br>");

        // template.print('Lead Phone : ' + gr.u_phone + "<br>");
        // template.print('Lead Email : ' + gr.u_email + "<br>");
        // template.print('Lead Company : ' + gr.u_company);
    }

})(current, template, email, email_action, event);
