sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here
    var gr1 = new GlideRecord('x_1386762_sales_cr_orders');
    gr1.addEncodedQuery('u_opportunityname.numberSTARTSWITH' + current.number);
    gr1.query();

    while (gr1.next()) {

        template.print('<b>Category:</b> ' + gr1.u_productcategory.getDisplayValue() + '<br>' +
            '<b>Item:</b>  ' + gr1.u_productitem.getDisplayValue() + '<br>' +
            '<b>Price:</b>  ' + gr1.u_productprice + '<br>' +
            '<b>Quantity:</b>  ' + gr1.u_quantity + '<br>' +
            '<b>Grand Total:</b>  ' + gr1.u_grandtotal + '<br>' +
            '<br>');
    }

})(current, template, email, email_action, event);
