sys_script_include.script:
var storegrandtotal = Class.create();
storegrandtotal.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


    storegrandtotal: function() {
        var value = this.getParameter('sysparm_value');
        var count = new GlideAggregate('x_1386762_sales_cr_orders');
        count.addEncodedQuery('u_opportunityname.number=' + value);

        //count.addQuery('sys_id', value );
        count.addAggregate('SUM', 'u_grandtotal');
        count.addAggregate('SUM', 'u_productprice');

        count.setGroup(false);
        count.query();

        var result = 0;
        var total = 0;

        while (count.next()) {
            result = count.getAggregate('SUM', 'u_grandtotal');
            total = count.getAggregate('SUM', 'u_productprice');

        }

        return result + "," + total;
    },

    type: 'storegrandtotal'
});
