sys_script_include.script:
var GroupByProductCategory = Class.create();
GroupByProductCategory.prototype = {
    initialize: function() {},

    getGroupMembers: function() {
        var category = current.u_productcategory.sys_id;
        var gm = new GlideRecord('x_1386762_sales_cr_product_items');
        gm.addQuery('u_productcategory', category);
        gm.query();

        var item ='';

		while(gm.next()){
			item+= ',' +gm.u_productitem.sys_id;
		}

		return 'sys_idIN' +item;


    },

    type: 'GroupByProductCategory'
};
